    init {
        for ((button, skill) in buttonPairs) {
            ButtonActions.onClick(button) {
                if (player.rights.anyMatch(PlayerRights.OWNER, PlayerRights.DEVELOPER) || player.gameMode.isSpawn) {
                    player.resetInteractions()
                    player.removeInterfaces()
                    player.enterSyntax = SetLevel(skill)
                    player.packetSender.sendEnterAmountPrompt("Please enter your desired " + skill.getName() + " level below.")
                    return@onClick
                }
                open(player, skill, 0)
            }
        }
        ButtonActions.onClick(36553, 36554, 36555, 36556, 36557, 36558, 36559, 36560, 36561, 36562, 36563, 36564, 36565, 36566) {
            val index = id - 36553
            if (player.hasAttribute(Attribute.SKILL_GUIDE_SELECTED_SKILL))
                open(player, player.getEnum<Skill>(Attribute.SKILL_GUIDE_SELECTED_SKILL), index)
        }
        ButtonActions.onClick(36104) {
            player.removeInterfaces()
        }
    }

    fun skillsInterface(player: Player, title: String?, category: String?, categories: Array<String?>, levels: IntArray, items: Array<Item?>, lines: Array<Array<String?>>) {
        val packetSender = player.packetSender
        packetSender.sendString(INTERFACE_ID + 2, title)
        packetSender.sendString(INTERFACE_ID + 3, category)
        var pos = INTERFACE_ID + 453
        for (i in 0..13) {
            if (i < categories.size) packetSender.sendString(pos++, categories[i]) else packetSender.sendString(pos++, "")
        }
        packetSender.sendInterfaceItems(INTERFACE_ID + 150, Arrays.asList(*items))
        pos = INTERFACE_ID + 51
        packetSender.clearInterfaceText(pos, pos + 400)
        for (i in levels.indices) {
            val level = levels[i]
            if (level > 0) packetSender.sendString(INTERFACE_ID + 51 + i * 4, Integer.toString(level))
            if (i >= lines.size) {
                packetSender.sendString(pos + i + 1, "")
                packetSender.sendString(pos + i + 2, "")
            } else {
                val subLines = lines[i]
                packetSender.sendString(pos + i + 1, if (subLines.size >= 1) subLines[0] else "")
                packetSender.sendString(pos + i + 2, if (subLines.size >= 2) subLines[1] else "")
            }
            pos += 3
        }
        packetSender.sendScrollbarHeight(INTERFACE_ID + 450, levels.size * 35 + 5)
        packetSender.sendInterface(INTERFACE_ID)
    }

    fun open(player: Player, skill: Skill, index: Int) {

        player.setEnum(Attribute.SKILL_GUIDE_SELECTED_SKILL, skill)

        player.packetSender.sendInterfaceScrollReset(INTERFACE_ID + 450)

        when (skill) {
            Skill.AGILITY -> agilityInterface(player, index)
            Skill.ATTACK -> atkInterface(player, index)
            Skill.CONSTRUCTION -> constructionInterface(player, index)
            Skill.COOKING -> cookingInterface(player, index)
            Skill.CRAFTING -> craftingInterface(player, index)
            Skill.DEFENCE -> defInterface(player, index)
            Skill.FIREMAKING -> firemakingInterface(player, index)
            Skill.FISHING -> fishingInterface(player, index)
            Skill.FLETCHING -> fletchingInterface(player, index)
            Skill.HERBLORE -> herbloreInterface(player, index)
            Skill.HITPOINTS -> hitpointsInterface(player, index)
            Skill.HUNTER -> hunterInterface(player, index)
            Skill.MAGIC -> magicInterface(player, index)
            Skill.MINING -> miningInterface(player, index)
            Skill.PRAYER -> prayerInterface(player, index)
            Skill.RANGED -> rangeInterface(player, index)
            Skill.RUNECRAFTING -> runecraftingInterface(player, index)
            Skill.SLAYER -> slayerInterface(player, index)
            Skill.SMITHING -> smithingInterface(player, index)
            Skill.STRENGTH -> strInterface(player, index)
            Skill.THIEVING -> thievingInterface(player, index)
            Skill.WOODCUTTING -> woodcuttingInterface(player, index)
            Skill.FARMING -> farmingInterface(player, index)
        }
    }

